mapper.select:
   SELECT * FROM Product
   where
   p_big_category = #{p_big_category}
	and p_middle_category = #{p_middle_category}
   order by p_no desc

SELECT * FROM Product WHERE ( p_maker like '%'||#{p_maker}||'%' and p_small_category like '%'||#{p_small_category}||'%'
and p_color IN #{color}
)
ORDER BY p_sale DESC
ORDER BY p_sale
ORDER BY p_no desc
